mapper.select:
SELECT COUNT(*)
FROM board
WHERE BOARD_STATUS = 'Y' AND CATE_NO = #{cateNo}

SELECT *
FROM board
WHERE BOARD_STATUS = 'Y'
AND BOARD_NO = #{boardNo}

    AND CATE_NO = #{cateNo}

mapper.update:
update board
set board_title = #{boardTitle}, board_content = #{boardContent}, CREATE_DATE = sysdate, CATE_NO = #{cateNo}
where board_NO = #{boardNo}

update board
set board_status = 'N'
where board_NO = #{bId}
