mapper.select:
select * 
from "USER"
where user_status = 'Y' and user_id = #{userId}

mapper.update:
UPDATE "USER"
SET 
    USER_ID = #{userId},
    USER_EMAIL = #{userEmail},
    USER_PHONE = #{userPhone}

    , USER_PWD = #{userPwd}

WHERE USER_NO = #{userNo}

mapper.select:
SELECT COUNT(*)
FROM "USER"
WHERE user_id = #{userId} AND user_no != #{currentUserNo}
